function units(score){var ct;if((score>=75)&&(score<=100)){ct=4.00}
else if(score>=70&&score<=74){ct=3.50}
else if(score>=65&&score<=69){ct=3.25}
else if(score>=60&&score<=64){ct=3.00}
else if(score>=55&&score<=59){ct=2.75}
else if(score>=50&&score<=54){ct=2.50}
else if(score>=45&&score<=49){ct=2.25}
else if(score>=40&&score<=44){ct=2.00}
else{ct=0.00}
return ct}
function grades(status){if((status>=0)&&(status<2.0)){return "Pass"}
else if((status>=2.0)&&(status<3.0)){return "Lower Credit"}
else if((status>=3.0)&&(status<3.5)){return "Upper Credit"}
else if((status>=3.5)&&(status<=4.0)){return "Distinction"}}